dataConfig.script:
	function FreeOrderProduct(row)
	{
		processFreeOrderId(row);
		
		return row;
    }
	function processFreeOrderId(row)
	{
		var freeOrderId = row.get('fo_parent_id');

		if (freeOrderId != null) {
			row.remove('free_order_id');
			row.put('freeOrderId', freeOrderId);
		}

		return row;
	}
